    category: string
}

import { Movies } from "../models/movies";
import { LIST_MOVIES, LIST_DATA_SUCCESS,MoviesActions,ADD_MOVIES,UPDATE_MOVIES }  from './movie.action';


export interface State {
  movies: Movies[];
}

const initialState: State = {
  movies: []
};

export function movieReducer(
  state = initialState,
  action: MoviesActions
)

{
    
  switch (action.type) {
    
    case LIST_MOVIES:
      return {
        ...state,
        movies: [...state.movies]
      };
      case LIST_DATA_SUCCESS: {
        return {
          movies: action.payload,
          message: null
        };
      }
      case ADD_MOVIES: {
        return {
          ...state
        };
        }
        case UPDATE_MOVIES: {
          return {
            ...state,
          };
        }
     default:
      return state;
  }
}

import { User } from '../../models/user';
import { AuthActionTypes, AuthAction } from '../actions/auth.actions';


export interface State {
  isAuthenticated: boolean;
  user: User | null;
  errorMessage: string | null;
}

export const initialState: State = {
    isAuthenticated: false,
    user: null,
    errorMessage: null
  };

  export function reducer(state = initialState, action: AuthAction): State {
    switch (action.type) {
      case AuthActionTypes.LOGIN_SUCCESS: {
        return {
          ...state,
          isAuthenticated: true,
          user: {
            token: action.payload.token,
            email: action.payload.email
          },
          errorMessage: null
        };
      }
      case AuthActionTypes.LOGIN_FAILURE: {
        return {
          ...state,
          errorMessage: 'Incorrect email and/or password.'
        };
      }
      case AuthActionTypes.SIGNUP_SUCCESS: {
        return {
          ...state,
          isAuthenticated: true,
          user: {
            token: action.payload.token,
            email: action.payload.email
          },
          errorMessage: null
        };
      }
      case AuthActionTypes.LOGOUT: {
        return initialState;
      }
      default: {
        return state;
      }
    }
  }

