import { Injectable } from "@angular/core";
import { Actions, Effect, ofType } from '@ngrx/effects';
import { Store } from '@ngrx/store';
import { HttpClient } from '@angular/common/http';
import { switchMap, map,  mergeMap } from 'rxjs/operators';
import { Observable } from 'rxjs';
import { Router } from "@angular/router";

import { ListDataSucess, LIST_MOVIES, ListMovies,AddMovie, ADD_MOVIES,UpdateMovies,UPDATE_MOVIES } from './movie.action';
import { Movies } from '../models/movies';
import { MovieService } from "../services/movie.service";

@Injectable()
export class MovieEffects {
    constructor(
        private actions: Actions,
        private movieService: MovieService,
        private router: Router
      ) {}

      @Effect({dispatch: true})
      ListMovies: Observable<any> =  this.actions.pipe(
                ofType(LIST_MOVIES),
                map((action: ListMovies) => action),
                mergeMap(payload => {
                    return this.movieService.getAll().pipe(
                        map((data) => new ListDataSucess(data)));
                }));

                @Effect({dispatch: true})
                CreateQuotes: Observable<any> =  this.actions.pipe(
                        ofType(ADD_MOVIES),
                        map((action: AddMovie) => action.payload),
                        mergeMap(payload => {
                            return this.movieService.addmovie(payload).pipe(
                                map((data) => {
                                    if (data) {
                                        return new AddMovie(data);
                                    }
                                }));
                        }));
                        
                        @Effect({dispatch: true})
                        UpdateQuotes: Observable<any> =  this.actions.pipe(
                                ofType(UPDATE_MOVIES),
                                map((action: UpdateMovies) => action.payload),
                                mergeMap(payload => {
                                    return this.movieService.update(1,payload).pipe(
                                        map((data) => {
                                            if (data) {
                                                return data;
                                            }
                                        }));
                                }));

}

import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Movies } from "../models/movies";
import { Observable, throwError } from 'rxjs';
import {catchError} from 'rxjs/operators';



@Injectable({
  providedIn: 'root'
})
export class MovieService {

  constructor(private http: HttpClient) { }

  baseUrl: string = "http://localhost:3000";
  httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json'
    })
  }

  getMovies(): Observable<Movies[]> {
    const url = `${this.baseUrl}/movies`; 
    return this.http.get<Movies[]>(url);

 }

  getById(id): Observable<Movies> {
    return this.http.get<Movies>(this.baseUrl + '/movies/' + id)
    .pipe(
      catchError(this.errorHandler)
    )
  }

  getAll(): Observable<Movies[]> {
    return this.http.get<Movies[]>(this.baseUrl + '/movies/')
    .pipe(
      catchError(this.errorHandler)
    )
  }

  addmovie(movie): Observable<Movies> {
    return this.http.post<Movies>(this.baseUrl + '/movies/', JSON.stringify(movie), this.httpOptions)
    .pipe(
      catchError(this.errorHandler)
    )
  }  

  update(id, movie): Observable<Movies> {
    return this.http.put<Movies>(this.baseUrl + '/movies/' + id, JSON.stringify(movie), this.httpOptions)
    .pipe(
      catchError(this.errorHandler)
    )
  }

  delete(id){
    return this.http.delete<Movies>(this.baseUrl + '/movies/' + id, this.httpOptions)
    .pipe(
      catchError(this.errorHandler)
    )
  }

  errorHandler(error) {
    let errorMessage = '';
    if(error.error instanceof ErrorEvent) {
      // Get client-side error
      errorMessage = error.error.message;
    } else {
      // Get server-side error
      errorMessage = `Error Code: ${error.status}\nMessage: ${error.message}`;
    }
    return throwError(errorMessage);
 }

}

import { Injectable } from '@angular/core';
import { Theme, light, dark } from '../theme';
@Injectable({
  providedIn: 'root'
})
export class ThemeService {

  constructor() { }
  private active: Theme = light;
  private availableThemes: Theme[] = [light, dark];

  getAvailableThemes(): Theme[] {
    return this.availableThemes;
  }

  getActiveTheme(): Theme {
    return this.active;
  }

  isDarkTheme(): boolean {
    return this.active.name === dark.name;
  }

  setDarkTheme(): void {
    this.setActiveTheme(dark);
  }

  setLightTheme(): void {
    this.setActiveTheme(light);
  }

  setActiveTheme(theme: Theme): void {
    this.active = theme;

    Object.keys(this.active.properties).forEach(property => {
      document.documentElement.style.setProperty(
        property,
        this.active.properties[property]
      );
    });
  }
}

